#include <iostream>#include <climits>int main(){	using namespace std;		cout << "int is " << sizeof(int) << " bytes." << endl;	cout << "short is " << sizeof(short) << " bytes." << endl;	cout << "long is " << sizeof(long) << " bytes." << endl;		cout << "maximum values:" << endl;	cout << "int:" << INT_MAX << endl;	cout << "short:" << SHRT_MAX << endl;	cout << "long:" << LONG_MAX << endl;		cout << "Minium int values = " << INT_MIN << endl;	cout << "Bits per byte = " << CHAR_BIT << endl;		return 0;}